from fastapi import FastAPIfrom pydantic import BaseModelimport joblibfrom pathlib import Pathimport reapp = FastAPI(title="NLP Sentiment Analysis API", version="1.0")model_path = Path.cwd().parent / 'Models' / "modelSVM.pkl"try:    model = joblib.load(model_path)    print(f"Model sucsessfully loaded: {model_path}")except Exception as e:    print(f"Error model loading! Please check model path: {e}")class PredictRequest(BaseModel):    text: str@app.get("/")async def root():    return {"mesaj": "API is working as expected."}@app.post("/predict")async def predict_sentiment(request: PredictRequest):    cleanText = re.sub(r'[^a-zA-Z\s]', '', request.text)    prediction = model.predict([cleanText])[0]        return {        "input_text": request.text,        "prediction": prediction    }